"glob:**/auto/c2b/**/**.scala" {
  align.tokens = [
    { code = "=>" },
    { code = "="  },
    { code = "<-" },
    { code = "->" },
    {
      code = ":"
      owners = [{
        regex = "Term\\.Param"
        parents = [ "Ctor\\.Primary" ]
      }]
    }
  ],
  align.multiline = true,
  verticalMultiline.atDefnSite = true,
  verticalMultiline.arityThreshold = 5
  newlines.implicitParamListModifierForce = [after],
  indent.caseSite = 5,
  align.openParenDefnSite = true
}
